INSERT INTO `cms_article` VALUES ('30', 'Client', 'package 上传下载;\n\nimport java.io.IOException;\nimport java.net.Socket;\nimport java.net.UnknownHostException;\n\npublic class Client {\npublic static void main(String[] args) throws UnknownHostException, IOException {\n	System.out.println(\"等待服务端连接\");\n	Socket s = new Socket(\"127.0.0.1\", 3333);\n	System.out.println(\"连接成功，开始操作\");\n	\n	new ClientLine(s).start();\n}\n}\n', 'a4e67010-66a5-467a-ba6d-2b3dd4a3f120.jpg', '2', null, '37', '66', '0', '1', '0', '2019-09-23 14:02:57', '2019-09-23 14:09:51', '2', '0000000000', '0');
INSERT INTO `cms_article` VALUES ('31', 'ClientLine', 'package 上传下载;\n\n\nimport java.io.DataOutputStream;\nimport java.io.File;\nimport java.io.FileInputStream;\nimport java.io.IOException;\nimport java.io.OutputStream;\nimport java.net.Socket;\nimport java.util.Scanner;\n\npublic class ClientLine extends Thread{\n	Socket socket;\n\n	public ClientLine(Socket socket) {\n		super();\n		this.socket = socket;\n	}\n	\n	@Override\n	public void run() {\n		Scanner sc = new Scanner(System.in);\n		System.out.print(\"请输入文件路径：\");\n		String path = sc.nextLine();\n		sc.close();\n		\n		try {\n			\n			File f  = new File(path);\n			FileInputStream fis = new FileInputStream(f);\n			\n			\n			OutputStream os = socket.getOutputStream();\n			DataOutputStream dos = new DataOutputStream(os);\n			\n			dos.writeUTF(f.getName());\n			dos.flush();\n			dos.writeInt((int)f.length());\n			dos.flush();\n			\n			byte[] b = new byte[100*1024];\n			while(true){\n				int count = fis.read(b);\n				if(count==-1)break;\n				dos.write(b, 0, count);\n				dos.flush();\n			}\n			fis.close();\n			dos.close();\n			os.close();\n			socket.close();\n			System.out.println(\"客户端上传文件工作完成！\");\n		} catch (IOException e) {\n			// TODO Auto-generated catch block\n			e.printStackTrace();\n		}\n	}\n	\n}\n', 'a500b380-076d-4ea7-a718-ee719fddda22.jpe', '4', null, '37', '66', '0', '1', '0', '2019-09-23 14:02:57', '2019-09-23 14:10:10', '0', '0000000000', '0');
INSERT INTO `cms_article` VALUES ('32', 'Server', 'package 上传下载;\n\nimport java.io.IOException;\nimport java.net.ServerSocket;\nimport java.net.Socket;\n\n\n\npublic class Server {\n	public static void main(String[] args) throws IOException {\n		System.out.println(\"正在连接客户端！\");\n		ServerSocket ss = new ServerSocket(3333);\n		Socket accept = ss.accept();\n		System.out.println(\"连接成功，开始操作\");\n		\n		new ServerLine(accept).start();\n	}\n}\n', 'a83dd543-d24b-46af-b687-09f4c77d6d39.jpg', '1', null, '37', '66', '0', '1', '0', '2019-09-23 14:02:57', '2019-09-23 14:09:56', '0', '0000000000', '0');
INSERT INTO `cms_article` VALUES ('33', 'ServerLine', 'package 上传下载;\n\nimport java.io.DataInputStream;\nimport java.io.FileOutputStream;\nimport java.io.IOException;\nimport java.io.InputStream;\nimport java.net.Socket;\n\npublic class ServerLine extends Thread{\n	Socket socket;\n\n	public ServerLine(Socket socket) {\n		super();\n		this.socket = socket;\n	}\n\n	\n	@Override\n	public void run() {\n		try {\n			InputStream in = socket.getInputStream();\n			DataInputStream dis = new DataInputStream(in);\n			\n			String url = \"D:\\\\建新文件\";\n			String fileName = dis.readUTF();\n			//System.out.println(fileName);\n			int length = dis.readInt();\n			FileOutputStream fos = new FileOutputStream(url+\"\\\\\"+fileName);\n			\n			byte[] b = new byte[100*1024];\n			\n			int len = 0;\n			while(true){\n				int count = dis.read(b);\n				if(count==-1)break;\n				len+=count;\n				fos.write(b, 0, count);\n			}\n			fos.flush();\n			fos.close();\n			System.out.println(\"源文件大小=\"+length+\",接收文件大小=\"+len+\",FileName=\"+fileName);\n			dis.close();\n			in.close();\n			socket.close();\n\n			System.out.println(\"服务器接收客户端下传文件工作完成\");\n		} catch (IOException e) {\n			// TODO Auto-generated catch block\n			e.printStackTrace();\n		}\n	}\n	\n}\n', 'ab059e24-13bc-4380-a674-bab2f0957d03.jpg', '6', null, '37', '66', '0', '1', '0', '2019-09-23 14:02:57', '2019-09-23 14:10:14', '380', '0000000001', '0');
